# import modules/library
from urllib.request import urlretrieve
from tqdm import trange
import time
import ctypes
import os
import shutil

# show in console function
def show_in_console():
    for i in trange(1):
        time.sleep(1)
    print("wallpaper.jpg download success")
    print("background changed")

# download wallpaper function
def download_wallpaper():
    time.sleep(1)
    urlretrieve("data:image/png;base64,","wallpaper.jpg")
    os.mkdir("/WannaDied")
    shutil.move("wallpaper.jpg","/WannaDied")

# set background function
def set_background():
    wallpaper_path = ("C:/WannaDied/wallpaper.jpg")
    image = ctypes.c_wchar_p(wallpaper_path)
    ctypes.windll.user32.SystemParametersInfoW(20,0,image,0)

# call all functions
show_in_console()
download_wallpaper()
set_background()